import React, { Component } from 'react';
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = { username: '' };
  }
  handleChange = (event) => {
    this.setState({username: event.target.value});
  }


  render() {
    return (
      <form>

      <p>Enter your name:</p>
      <input type='text'onChange={this.handleChange} />
      </form>
    );
  }




}
