import React, { Component } from 'react';
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {dogName: '', dogBreed: '',bDay: ''};

    this.handleName = this.handleName.bind(this)
    this.handleBreed = this.handleBreed.bind(this)
    this.handlebDay= this.handl.bDay.bind(this)
  }

  handleName= (event) =>{
    this.setState({dogName: event.target.value});
  }

  handleBreed = (event) => { 
    this.setState({dogBreed: event.target.value});
  }
  handlebBay = (event) => {
    this.setState({bDay: event.target.value});
  }


  render() {
    return (
      <form>
      NAME
      <input type='text' onChange={this.handleName} />
      BREED
      <input type='text' onChange={this.handleBreed} />
      <input type='date' onChange={this.handlebDay} />

      </form>
    );
  }




}
