import React, { Component } from 'react';
import './App.css';

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = {dogName: ''};
    this.state = {dogBreed: ''};

    this.handleName = this.handleChange.bind(this)
    this.handleBreed = this.handleSubmit.bind(this)


  }

  handleName= (event) =>{
    this.setState({dogName: event.target.value});
  }

  handleBreed = (event) => {
    this.setState({dogBreed: event.target.value});
  }


  render() {
    return (
      <form>
      NAME
      <input type='text'onChange={this.handleChange} />
      BREED
      <input type='text'onChange={this.handleChange} />

      </form>
    );
  }




}
